# Disable mapreduce speculative execution.

SET mapreduce.map.speculative=false;
SET mapreduce.reduce.speculative=false;

# This improves the parallelism for smaller datasets

SET hive.merge.mapfiles=true;
SET hive.merge.mapredfiles=true;
SET hive.merge.size.per.task=3145728;
SET hive.merge.smallfiles.avgsize=3145728;
SET mapred.max.split.size=3145728;{cursor}
